mapper.select:
select MEM_VAC_DATE, MEM_TOTAL_DATE
	from member
	where mem_no=#{memNo}

mapper.update:
update member
	set
	MEM_USED_DATE = MEM_USED_DATE+#{leaveCount}
	,MEM_TOTAL_DATE =MEM_VAC_DATE-#{leaveCount}
where mem_no = #{memNo}

update appr_accept
	set
	status = #{status}
	,appr_memo=#{memo}
	,appr_date=sysdate
where doc_no = #{docNo}
and mem_no = #{memNo}

update approval
	set
	 status = #{status}
	where doc_no = #{docNo}

update reference
	set
	 read = 'Y'
	where doc_no = #{docNo}
	and ref_no = #{memNo}

